package fr.hyriode.bedwars.game.npc;

import fr.hyriode.hyrame.language.HyriLanguageMessage;
import fr.hyriode.hyrame.npc.NPCSkin;
import fr.hyriode.bedwars.HyriBedWars;

public enum EHyriBWNPCType {
    SHOP(new NPCSkin("",
            ""),
            "npc.shop.name"),
    UPGRADE(new NPCSkin("",
            ""),
            "npc.upgrade.name"),
    COAL_SHOP(new NPCSkin("",
            ""),
            "npc.coal_shop.name"),
    ;

    private final NPCSkin defaultSkin;
    private final String keyName;

    EHyriBWNPCType(NPCSkin defaultSkin, String keyName){
        this.defaultSkin = defaultSkin;
        this.keyName = keyName;
    }

    public NPCSkin getDefaultSkin() {
        return defaultSkin;
    }

    public String getKeyName() {
        return keyName;
    }

    public HyriLanguageMessage getLanguageName(){
        return HyriBedWars.getLanguageManager().getMessage(this.keyName);
    }
}
